import React, { Component } from 'react';
import './Widget.css';

class Wid3 extends Component {
      state = {
          post : {}
        }
    
    componentDidMount() {
      fetch(this.props.url)
      .then(data => data.json())
      .then(data => this.setState({data}))
    }

    componentDidUpdate() {
      console.log("The component just updated")
    }

    render(){
        return(
            <div className="Widget">
                {this.state.post.main}
            </div>
        )
    }
}

export default Wid3;
