import React, { Component } from 'react';
import './Widget.css';

class Wid3 extends Component {
    constructor(props){
        super(props);
        let res = this.state = {
            post : {}
          }
    }
    
    componentDidMount(){
        fetch(this.props.url) // On récupère l'URL de notre API
        .then((response) => {
          return response.json() // Format json
        })
        .then((result) => {
          this.setState({post: result}) // setState pour mettre notre resultat dans le state "post"
        })
      }

    render(){
        return(
            <div className="Widget">
                {this.state.post}
            </div>
        )
    }
}

export default Wid3;
