    render() {
      return (
        <div>
          <h1>Stock Market</h1>
          <Plot
            data={[
              {
                x: this.state.stockChartXValues,
                y: this.state.stockChartYValues,
                type: 'scatter',
                mode: 'lines+markers',
                marker: {color: 'red'},
              }
            ]}
            layout={{width: 720, height: 440, title: 'A Fancy Plot'}}
          />
        </div>
      )
    }
  }
  
  //*export default Stock;

export default Wid4;
