import React, { Component } from 'react';
import './Widget.css';

const API_URL_ICON = "http://openweathermap.org/img/wn/";

class Wid3 extends Component {
      state = {
          data : []
        }

    componentDidMount() {
      fetch(this.props.url)
      .then(data => data.json())
      .then(data => this.setState({data}))
    }

    componentDidUpdate() {
      console.log("The component just updated")
    }

    getHTMLElementFromIcon(icon){ 
      return `<img src=${API_URL_ICON}${icon}@2x.png class="weather-icon"/>`
    }
    
    render() {
      //console.log(this.state.data);
      const data = this.state.data;
      if(data && data.main && data.weather) {
        return (
          <div className="Widget">
            <h1>{data.name}</h1>
            <h3>{data.weather[0].description}</h3>
            <h3>Température {data.main.temp}° C</h3>
            <h3>Ressentie {data.main.feels_like}° C</h3>
            <div id= "test">{this.getHTMLElementFromIcon(data.weather[0].icon)}</div>
          </div>
        )

      }else{
        return (<div>Loading...</div>)
      }
    }
}

document.getElementById("test").innerHTML = this.getHTMLElementFromIcon(this.data.weather[0].icon);
export default Wid3;
