import React, { Component } from 'react';
import './Widget.css';
import './news.css';

class Wid5 extends Component {
      state = {
        data: []
            }

    componentDidMount() {
        fetch(this.props.url)
        .then(data => data.json())
        .then(data => this.setState({data}))
    }

    componentDidUpdate() {
      console.log("The component just updated")
    }

    render() {
      const data = this.state.data;
      const dataList = data.context;

      console.log(data)

      if(data && dataList) {
        return (
          <div>
            <p>{data.departures[0].display_informations.direction}{data.context.current_datetime}</p>
            <p>{data.departures[1].display_informations.direction}</p>
            <p>{data.departures[2].display_informations.direction}</p>
            <p>{data.departures[3].display_informations.direction}</p>
            <p>{data.departures[4].display_informations.direction}</p>
          </div>
        )
      }else{
        return (<div>Loading...</div>)
      }
    }
}

export default Wid5;
