import React, { Component } from 'react';
import './Widget.css';
import './news.css';

class Wid5 extends Component {
      state = {
        data: []
            }

    componentDidMount() {
        fetch(this.props.url)
        .then(data => data.json())
        .then(data => this.setState({data}))
    }

    componentDidUpdate() {
      console.log("The component just updated")
    }

    render() {
      const data = this.state.data;
      const dataList = data.context;

      console.log(data)
      console.log(this.props.url)

      if(data && dataList) {
        return (
          <div>
            <p><img src="Photos/Metro.svg" alt="Metro" title="Metro" />{data.departures[0].display_informations.direction} {data.departures[0].stop_date_time.arrival_date_time}</p>
            <p>{data.departures[1].display_informations.direction} {data.departures[1].stop_date_time.arrival_date_time}</p>
            <p>{data.departures[2].display_informations.direction} {data.departures[2].stop_date_time.arrival_date_time}</p>
            <p>{data.departures[3].display_informations.direction} {data.departures[3].stop_date_time.arrival_date_time}</p>
            <p>{data.departures[4].display_informations.direction} {data.departures[4].stop_date_time.arrival_date_time}</p>
          </div>
        )
      }else{
        return (<div>Loading...</div>)
      }
    }
}

export default Wid5;
