import React, { Component } from 'react';
import './Widget.css';


class Wid1 extends Component {
    state = {
      data : []
    }
  
  componentDidMount(){
    //console.log(this.props.url);
    fetch(this.props.url)
    .then(data => data.json())
    .then(data => this.setState({data}))
  }

  componentDidUpdate() {
    console.log("The component just updated")
  }

  render(){
    
      const news = this.state.data;
     //console.log(news);
      //const icon = `<img src=${API_URL_ICON}${icon}@2x.png class="weather-icon"/>`;
      if(news && news.articles) {
        return (
          <div class="container">
      <div class="row">
          <div class="col-sm-12">
 
 
              <div id="my-slider" class="carousel slide" date-ride="carousel">
               
              
              <div class="carousel-inner" role="listbox">
                <div class="item active">
                <h2>{news.articles[0].title}</h2>
                 </div>
                     
 
                    <div class="item">
                    <h2>{news.articles[1].title}</h2>
                   
                     
                </div>
 
              </div>
 
 
              <a class="left carousel-control" href="#my-slider" role="button" data-slider="prev">
                <span class="glyphicon glyphicon-chevron-left" aria-hidden="true"></span>
                <span class="sr-only">Previous</span>
 
              </a>
 
                <a class="right carousel-control" href="#my-slider" role="button" data-slider="next">
                <span class="glyphicon glyphicon-chevron-right" aria-hidden="true"></span>
                <span class="sr-only">Previous</span>
 
              </a>
 
 
</div>
</div>
</div>
 </div>
         
        )
      }else{
        return (<div>Loading...</div>)
      }
    }
}

export default Wid1;
