import React, { Component } from 'react';
import './Widget.css';
import metro from './metro.png';
import num from './6.png';

class Wid5 extends Component {
      state = {
        data: []
            }

    componentDidMount() {
        fetch(this.props.url)
        .then(data => data.json())
        .then(data => this.setState({data}))
    }

    componentDidUpdate() {
      console.log("The component just updated")
    }

    render() {
      const data = this.state.data;
      const dataList = data.context;

      console.log(data)
      console.log(this.props.url)

      if(data && dataList) {
        return (
          <div>
            <div>
              <img src={metro} id="metro"/><img src={num} id="metro"/>
              {data.departures[0].stop_date_time.arrival_date_time.substr(9, 2)}:{data.departures[0].stop_date_time.arrival_date_time.substr(11, 2)}-{data.departures[0].stop_point.name.substr(0, 10)}{data.departures[0].display_informations.direction}
            </div>
            <div>
              <img src={metro} id="metro"/><img src={num} id="metro"/>
              {data.departures[1].stop_date_time.arrival_date_time.substr(9, 2)}:{data.departures[1].stop_date_time.arrival_date_time.substr(11, 2)}-{data.departures[1].stop_point.name.substr(0, 10)}
            </div>
            <div>
              <img src={metro} id="metro"/><img src={num} id="metro"/>
              {data.departures[2].stop_date_time.arrival_date_time.substr(9, 2)}:{data.departures[2].stop_date_time.arrival_date_time.substr(11, 2)}-{data.departures[2].stop_point.name.substr(0, 10)}
            </div>
            <div>
              <img src={metro} id="metro"/><img src={num} id="metro"/>
              {data.departures[3].stop_date_time.arrival_date_time.substr(9, 2)}:{data.departures[3].stop_date_time.arrival_date_time.substr(11, 2)}-{data.departures[3].stop_point.name.substr(0, 10)}
            </div>
            <div>
              <img src={metro} id="metro"/><img src={num} id="metro"/>
              {data.departures[4].stop_date_time.arrival_date_time.substr(9, 2)}:{data.departures[4].stop_date_time.arrival_date_time.substr(11, 2)}-{data.departures[4].stop_point.name.substr(0, 10)}
            </div>
          </div>
        )
      }else{
        return (<div>Loading...</div>)
      }
    }
}

export default Wid5;
